 const nodemailer = require('nodemailer');
function send(sender,receiver,password,message){
    const transporter = nodemailer.createTransport({
        host: 'smtp.mail.ru',
        port: 465 ,
        secure:true,
        auth: {
            user: sender,
            pass: password
        }
    });
     const Message = {
         //from :'Mailer test <elfrieda26@ethereal.email>',
         from : sender,
         to: receiver,
         subject: "Lab5",
         text: message,
     }
    transporter.sendMail(Message,(err,info)=>{
        if(err)console.log(err)
        console.log('Message sent: ',info);
    })
 }
module.exports = send;